import React, { useRef, useEffect, useState } from 'react';
import './LanternEffect.css';

const LanternEffect = ({ children }) => {
  const lanternRef = useRef();
  const [isLanternMode, setIsLanternMode] = useState(true);

  useEffect(() => {
    const lantern = lanternRef.current;
    if (!lantern) return;
    
    function moveLantern(e) {
      if (!isLanternMode) return;
      
      let x, y;
      if (e.touches && e.touches.length) {
        x = e.touches[0].clientX;
        y = e.touches[0].clientY;
      } else {
        x = e.clientX;
        y = e.clientY;
      }
      lantern.style.setProperty('--lx', x + 'px');
      lantern.style.setProperty('--ly', y + 'px');
    }
    
    window.addEventListener('mousemove', moveLantern);
    window.addEventListener('touchmove', moveLantern);
    return () => {
      window.removeEventListener('mousemove', moveLantern);
      window.removeEventListener('touchmove', moveLantern);
    };
  }, [isLanternMode]);

  return (
    <div className="lantern-effect" ref={lanternRef}>
      {children}
      <div className={`lantern-overlay ${!isLanternMode ? 'full-light' : ''}`} />
      <div
        className={`light-switch-physical${isLanternMode ? '' : ' off'}`}
        onClick={() => setIsLanternMode(!isLanternMode)}
        title={isLanternMode ? "Acender as luzes" : "Usar lanterna"}
      >
        <div className="switch-body">
          <div className="switch-toggle">
            <span className="switch-label switch-on">I</span>
            <span className="switch-label switch-off">O</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LanternEffect;
