import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './ClueMap.css';

const ClueMap = ({ projetos = [], unlocked = [] }) => {
  const pointsRef = useRef([]);
  const linesRef = useRef([]);

  useEffect(() => {
    // Animação dos pontos
    pointsRef.current.forEach((el, i) => {
      if (el) {
        gsap.fromTo(
          el,
          { scale: 0.7, opacity: 0.5 },
          { scale: 1, opacity: 1, duration: 0.7, delay: i * 0.08, ease: 'back.out(2)' }
        );
      }
    });
    // Animação das linhas
    linesRef.current.forEach((el, i) => {
      if (el) {
        gsap.fromTo(
          el,
          { strokeDasharray: 100, strokeDashoffset: 100 },
          { strokeDashoffset: 0, duration: 1, delay: i * 0.12, ease: 'power2.out' }
        );
      }
    });
  }, []);

  // Layout circular
  const R = 110;
  const cx = 140, cy = 140;
  const angleStep = (2 * Math.PI) / (projetos.length || 1);
  const points = projetos.map((_, i) => [
    cx + R * Math.cos(i * angleStep - Math.PI / 2),
    cy + R * Math.sin(i * angleStep - Math.PI / 2),
  ]);

  return (
    <div className="clue-map">
      <svg width={280} height={280}>
        {/* Linhas conectando pontos */}
        {points.map((p, i) => (
          <line
            key={i}
            ref={el => (linesRef.current[i] = el)}
            x1={points[i][0]}
            y1={points[i][1]}
            x2={points[(i + 1) % points.length][0]}
            y2={points[(i + 1) % points.length][1]}
            stroke="#bfa77a"
            strokeWidth={3}
            opacity={0.5}
          />
        ))}
        {/* Pontos */}
        {points.map(([x, y], i) => (
          <circle
            key={i}
            ref={el => (pointsRef.current[i] = el)}
            cx={x}
            cy={y}
            r={unlocked.includes(projetos[i].id) ? 13 : 9}
            fill={unlocked.includes(projetos[i].id) ? '#ff2a2a' : '#bfa77a'}
            stroke="#fffbe6"
            strokeWidth={unlocked.includes(projetos[i].id) ? 4 : 2}
            opacity={unlocked.includes(projetos[i].id) ? 1 : 0.7}
          />
        ))}
      </svg>
      <div className="clue-map-label">Mapa de Pistas</div>
    </div>
  );
};

export default ClueMap;
