import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './Corkboard.css';
import projects from '../data/projects.json';

const Corkboard = ({ onCaseClick, isLanternMode }) => {
  const boardRef = useRef();
  const cardsRef = useRef([]);

  useEffect(() => {
    if (boardRef.current) {
      // Animação de entrada do quadro
      gsap.fromTo(
        boardRef.current,
        { opacity: 0, y: 30 },
        { opacity: 1, y: 0, duration: 1, ease: 'power3.out' }
      );

      // Animação dos cartões
      cardsRef.current.forEach((card, index) => {
        if (card) {
          gsap.fromTo(
            card,
            {
              opacity: 0,
              scale: 0.8,
              rotation: gsap.utils.random(-5, 5),
              y: 50
            },
            {
              opacity: 1,
              scale: 1,
              y: 0,
              duration: 0.8,
              delay: 0.2 + index * 0.1,
              ease: 'back.out(1.2)'
            }
          );
        }
      });
    }
  }, []);

  useEffect(() => {
    const board = boardRef.current;
    if (!board || !isLanternMode) return;

    const moveLanternOnBoard = (e) => {
      const rect = board.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      board.style.setProperty('--cork-lx', `${x}px`);
      board.style.setProperty('--cork-ly', `${y}px`);
    };

    window.addEventListener('mousemove', moveLanternOnBoard);

    return () => {
      window.removeEventListener('mousemove', moveLanternOnBoard);
    };
  }, [isLanternMode]);

  return (
    <div className={`corkboard-container ${isLanternMode ? 'lantern-mode' : 'full-light-mode'}`} ref={boardRef}>
      <div className="corkboard">
        {projects.map((project, index) => (
          <div
            key={project.titulo}
            className="case-card"
            ref={el => cardsRef.current[index] = el}
            onClick={() => onCaseClick(project)}
          >
            <div className="pin"></div>
            <img
              src={process.env.PUBLIC_URL + '/images/' + project.imagem}
              alt={project.titulo}
              className="case-image"
            />
            <div className="case-info">
              <h3>{project.titulo}</h3>
              <p>{project.descricao}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Corkboard;
