import React, { useEffect, useRef, useState } from 'react';
import Particles from 'react-tsparticles';
import { gsap } from 'gsap';
import StartButton from './components/StartButton';
import Corkboard from './components/Corkboard';
import ClueMap from './components/ClueMap';
import AboutSofia from './components/AboutSofia';
import ContactForm from './components/ContactForm';
import Extras from './components/Extras';
import ParticlesRain from './components/ParticlesRain';
import './index.css';
import './styles/main.css';

function App() {
  const signRef = useRef();
  const [started, setStarted] = useState(false);
  const [unlocked, setUnlocked] = useState([]);

  const projetos = [
    {
      titulo: 'O Observador Vermelho',
      descricao: 'Uma investigação sobre identidades ocultas e o olhar que tudo vê.',
      imagem: 'IMG-20250619-WA0101.jpg',
    },
    {
      titulo: 'Matou - O Rato Diretor',
      descricao: 'O mistério dos bastidores de um filme inusitado.',
      imagem: 'IMG-20250619-WA0100.jpg',
    },
    {
      titulo: 'A Testemunha do Fogo',
      descricao: 'Relato sombrio de quem viu demais em meio ao caos.',
      imagem: 'IMG-20250619-WA0099.jpg',
    },
    {
      titulo: 'Dupla Natureza',
      descricao: 'Dois lados, um só coração: o caso dos gêmeos improváveis.',
      imagem: 'IMG-20250619-WA0098.jpg',
    },
    {
      titulo: 'O Enigma do Olhar Circular',
      descricao: 'Uma figura misteriosa que desafia a lógica e a percepção.',
      imagem: 'IMG-20250619-WA0097.jpg',
    },
    {
      titulo: 'A Sereia de Prata',
      descricao: 'Beleza e mistério em um cenário de tons rubros e prateados.',
      imagem: 'IMG-20250619-WA0096.jpg',
    },
    {
      titulo: 'Hello, S4tur0',
      descricao: 'Uma saudação enigmática vinda do outro lado da janela.',
      imagem: 'IMG-20250619-WA0095.jpg',
    },
    {
      titulo: 'O Circo dos Olhos Dourados',
      descricao: 'Entre o riso e o medo, um espetáculo de segredos.',
      imagem: 'IMG-20250619-WA0094.jpg',
    },
    {
      titulo: 'A Guardiã Carmesim',
      descricao: 'Força e magia em um universo de fantasia e poder.',
      imagem: 'IMG-20250619-WA0093.jpg',
    },
    {
      titulo: 'A Festa do Mistério',
      descricao: 'Noite de segredos sob estrelas e chapéus de festa.',
      imagem: 'IMG-20250619-WA0092.jpg',
    },
    {
      titulo: 'O Sorriso Azul',
      descricao: 'Uma criatura travessa e encantadora, perdida no céu.',
      imagem: 'IMG-20250619-WA0091.jpg',
    },
  ];

  const handleUnlock = idx => {
    setUnlocked(prev => (prev.includes(idx) ? prev : [...prev, idx]));
  };

  useEffect(() => {
    if (signRef.current) {
      gsap.fromTo(signRef.current, { opacity: 0, y: -60 }, { opacity: 1, y: 0, duration: 1.5, ease: 'power3.out' });
    }
  }, []);

  return (
    <div className="App">
      <ParticlesRain />
      {/* Partículas de cinzas/chuva */}
      <Particles
        id="tsparticles"
        options={{
          background: { color: '#111' },
          fpsLimit: 60,
          particles: {
            number: { value: 80, density: { enable: true, value_area: 800 } },
            color: { value: '#bfa77a' },
            opacity: { value: 0.18 },
            size: { value: 2 },
            move: { enable: true, speed: 0.7, direction: 'bottom', straight: false },
            shape: { type: 'circle' },
          },
          detectRetina: true,
        }}
      />
      {/* Letreiro animado */}
      <header id="neon-sign" ref={signRef}>
        <h1>
          <span className="neon">Sofia Peixoto Barbosa</span><br />
          <span className="subtitle">Artista dos Mistérios</span>
        </h1>
      </header>
      {/* Botão de início */}
      {!started && <StartButton onClick={() => setStarted(true)} />}
      {/* Quadro de cortiça, mapa de pistas, etc. */}
      {started && (
        <div className="main-board-flex">
          <Corkboard projetos={projetos} onUnlock={handleUnlock} />
          <ClueMap unlocked={unlocked} />
        </div>
      )}
      {started && <AboutSofia />}
      {started && <ContactForm />}
      {started && <Extras />}
    </div>
  );
}

export default App;
