import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import CaseModal from './CaseModal';
import './Corkboard.css';

const Corkboard = ({ projetos, onUnlock }) => {
  const postersRef = useRef([]);
  const [modalProjeto, setModalProjeto] = useState(null);
  const [modalIdx, setModalIdx] = useState(null);

  useEffect(() => {
    postersRef.current.forEach((el, i) => {
      if (el) {
        gsap.fromTo(
          el,
          { opacity: 0, y: 40, scale: 0.9 },
          { opacity: 1, y: 0, scale: 1, duration: 1, delay: i * 0.15, ease: 'power3.out' }
        );
      }
    });
  }, [projetos]);

  const handleOpenModal = (projeto, idx) => {
    setModalProjeto(projeto);
    setModalIdx(idx);
    if (onUnlock) onUnlock(idx);
  };

  return (
    <section className="corkboard">
      {projetos.map((projeto, idx) => (
        <div
          className="poster noir-poster"
          key={projeto.titulo}
          ref={el => (postersRef.current[idx] = el)}
          onClick={() => handleOpenModal(projeto, idx)}
        >
          <div className="pin"></div>
          <img
            src={process.env.PUBLIC_URL + '/images/' + projeto.imagem}
            alt={projeto.titulo}
            className="poster-img"
          />
          <div className="poster-title">{projeto.titulo}</div>
          <div className="poster-desc">{projeto.descricao}</div>
        </div>
      ))}
      {modalProjeto && (
        <CaseModal projeto={modalProjeto} onClose={() => setModalProjeto(null)} />
      )}
    </section>
  );
};

export default Corkboard;
