import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './AboutSofia.css';

const AboutSofia = () => {
  const aboutRef = useRef();
  const curtainRef = useRef();

  useEffect(() => {
    if (curtainRef.current) {
      gsap.to(curtainRef.current, {
        height: 0,
        duration: 1.2,
        delay: 0.5,
        ease: 'power2.inOut',
      });
    }
    if (aboutRef.current) {
      gsap.fromTo(
        aboutRef.current,
        { opacity: 0, y: 40 },
        { opacity: 1, y: 0, duration: 1.2, delay: 1, ease: 'power3.out' }
      );
    }
  }, []);

  return (
    <section className="about-sofia-section">
      <div className="about-curtain" ref={curtainRef}></div>
      <div className="about-sofia" ref={aboutRef}>
        <div className="about-photo-frame">
          {/* Substitua pelo src real da foto de Sofia se disponível */}
          <img src={process.env.PUBLIC_URL + '/images/sofia.jpg'} alt="Sofia Peixoto Barbosa" className="about-photo" />
        </div>
        <div className="about-text">
          <h2>Arquivo Pessoal: Sofia Peixoto Barbosa</h2>
          <p className="about-narrative">
            <span className="about-label">CONFIDENCIAL</span><br/>
            Nome: <b>Sofia Peixoto Barbosa</b><br/>
            Profissão: Artista Visual & Investigadora de Mistérios<br/>
            <br/>
            Relatório: <br/>
            "Dizem que, nas sombras dos becos e entre as luzes trêmulas dos postes, Sofia encontra inspiração para criar universos onde a arte e o enigma se entrelaçam. Com olhar atento e mãos habilidosas, ela transforma pistas em poesia visual, mistérios em cor e textura. Cada obra é um caso resolvido, cada traço, uma pista deixada para quem ousa investigar mais fundo."
          </p>
        </div>
      </div>
    </section>
  );
};

export default AboutSofia;
